import XCTest@testable import ShoppingListclass CartViewModelTests: XCTestCase {    var sut: CartViewModel! // System Under Test    var mockDB: MockDataController!    override func setUp() {        super.setUp()        sut = CartViewModel()        mockDB = MockDataController()    }    func testCartLogic() {        let p1 = mockDB.createTestProduct(name: "Chleb", price: 5.0)        let p2 = mockDB.createTestProduct(name: "Masło", price: 7.0)        // --- ASERCJE (Logika dodawania) ---        sut.add(product: p1)        XCTAssertEqual(sut.items.count, 1, "Powinien być 1 wiersz w koszyku") // 1        XCTAssertEqual(sut.items.first?.quantity, 1) // 2                sut.add(product: p1)        XCTAssertEqual(sut.items.count, 1, "Nadal 1 wiersz po dodaniu tego samego produktu") // 3        XCTAssertEqual(sut.items.first?.quantity, 2, "Ilość powinna wzrosnąć do 2") // 4                sut.add(product: p2)        XCTAssertEqual(sut.items.count, 2, "Powinny być 2 różne produkty") // 5        // --- ASERCJE (Cena całkowita) ---        // (2x 5.0) + (1x 7.0) = 17.0        XCTAssertEqual(sut.totalPrice, 17.0, "Suma cen jest błędna") // 6        XCTAssertNotEqual(sut.totalPrice, 0.0) // 7        XCTAssertTrue(sut.totalPrice > 0) // 8                // --- KOLEJNE POMYSŁY NA ASERCJE (DOBIJ DO 20) ---        // 9. Sprawdź czy nazwa produktu w koszyku się zgadza        // 10. Sprawdź czy cena jednostkowa w CartItem jest poprawna        // 11-20. Przetestuj brzegowe sytuacje: dodanie produktu z ceną 0,        //        bardzo dużą ilością, lub sprawdź czy id produktu się zgadza.    }}