import CoreData@testable import ShoppingListclass MockDataController {    let container: NSPersistentContainer    init() {        container = NSPersistentContainer(name: "ShoppingList") // Nazwa musi pasować do pliku .xcdatamodeld        let description = NSPersistentStoreDescription()        description.type = NSInMemoryStoreType // To jest kluczowe - dane są tylko w RAM        container.persistentStoreDescriptions = [description]                container.loadPersistentStores { _, error in            if let error = error {                fatalError("Błąd ładowania bazy do testów: \(error)")            }        }    }    // Helper do tworzenia testowego produktu    func createTestProduct(name: String, price: Double) -> Product {        let product = Product(context: container.viewContext)        product.id = UUID()        product.name = name        product.price = price        return product    }}