struct PaymentView: View {    @StateObject var viewModel: PaymentViewModel    var body: some View {        VStack(spacing: 20) {            Text(viewModel.product.name)                .font(.title)            Text("Cena: \(viewModel.product.price / 100) zł")            Button("Zapłać") {                Task {                    await viewModel.pay()                }            }            .buttonStyle(.borderedProminent)            if let status = viewModel.paymentStatus {                Text("Status: \(status.rawValue)")                    .foregroundColor(status == .paid ? .green : .red)            }        }        .padding()    }}